pragma solidity =0.7.6;
pragma abicoder v2;

import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Factory.sol";
import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Pair.sol";
import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Callee.sol";
import "@uniswap/v2-periphery/contracts/interfaces/IUniswapV2Router02.sol";
import "@uniswap/v3-core/contracts/interfaces/IUniswapV3Factory.sol";
import "@uniswap/v3-core/contracts/interfaces/IUniswapV3Pool.sol";
import "@uniswap/v3-core/contracts/libraries/LowGasSafeMath.sol";
import "./library/CallbackValidation.sol";
import "@uniswap/v3-core/contracts/interfaces/callback/IUniswapV3FlashCallback.sol";
import "@uniswap/v3-periphery/contracts/libraries/TransferHelper.sol";
import "@uniswap/v3-periphery/contracts/base/PeripheryPayments.sol";
import "@uniswap/swap-router-contracts/contracts/interfaces/IV3SwapRouter.sol";

import "./interfaces/ISwapRouterExtended.sol";
import "hardhat/console.sol";
import "./library/UniswapCallbackData.sol";

contract Arbitrageur is
    IUniswapV2Callee,
    IUniswapV3FlashCallback,
    PeripheryPayments
{
    using LowGasSafeMath for uint;
    using LowGasSafeMath for int;

    ISwapRouterExtended immutable routerV3;
    IUniswapV2Router02 immutable routerV2;

    constructor(ISwapRouterExtended _routerV3, IUniswapV2Router02 _routerV2)
        PeripheryImmutableState(_routerV3.factory(), _routerV3.WETH9())
    {
        routerV3 = _routerV3;
        routerV2 = _routerV2;
    }

    function arbitrageV2toV3(
        uint amount,
        address baseToken,
        address pair,
        address pool
    ) external {
        require(pair != address(0), "!pair");

        address token0 = IUniswapV2Pair(pair).token0();
        address token1 = IUniswapV2Pair(pair).token1();
        uint amount0Out = baseToken == token0 ? amount : 0;
        uint amount1Out = baseToken == token1 ? amount : 0;

        // need to pass some data to trigger uniswapV2Call
        bytes memory data = abi.encode(
            FlashV2CallbackData({
                baseToken: baseToken,
                pool: pool,
                payer: msg.sender
            })
        );

        IUniswapV2Pair(pair).swap(amount0Out, amount1Out, address(this), data);
    }

    function arbitrageV3toV2(
        uint amount,
        address baseToken,
        address pair,
        address pool
    ) external {
        address token0 = IUniswapV3Pool(pool).token0();
        address token1 = IUniswapV3Pool(pool).token1();
        IUniswapV3Pool(pool).flash(
            address(this),
            baseToken == token0 ? amount : 0,
            baseToken == token0 ? 0 : amount,
            abi.encode(
                FlashV3CallbackData({
                    baseToken: baseToken,
                    pair: pair,
                    payer: msg.sender,
                    amount: amount
                })
            )
        );
    }

    // as of now calling UniV2-UniV3 is kinda ~ hardcoded
    // will need to put more info in the data like dex-index or somthng like this
    // and again - as of now, this callback will execute v3 trade, and v3 callback -> v2 trade
    function uniswapV2Call(
        address sender,
        uint amount0,
        uint amount1,
        bytes calldata data
    ) external override {
        IUniswapV2Pair pair = IUniswapV2Pair(msg.sender);
        address token0 = pair.token0();
        address token1 = pair.token1();
        FlashV2CallbackData memory decoded = abi.decode(
            data,
            (FlashV2CallbackData)
        );
        require(
            msg.sender ==
                IUniswapV2Factory(routerV2.factory()).getPair(token0, token1),
            "!sender"
        );
        uint amountToBorrow = amount0 == 0 ? amount1 : amount0;

        uint fee = ((amountToBorrow * 3) / 997) + 1;
        uint amountOwed = amountToBorrow + fee;

        uint amountRecieved;

        TransferHelper.safeApprove(
            decoded.baseToken,
            address(routerV3),
            amountToBorrow
        );

        amountRecieved = routerV3.exactInputSingle(
            ISwapRouter.ExactInputSingleParams({
                tokenIn: token0 == decoded.baseToken ? token1 : token0,
                tokenOut: token0 == decoded.baseToken ? token0 : token1,
                fee: IUniswapV3Pool(decoded.pool).fee(),
                recipient: address(this),
                deadline: block.timestamp,
                amountIn: amountToBorrow,
                amountOutMinimum: 0,
                sqrtPriceLimitX96: 0
            })
        );
        require(amountRecieved > amountOwed, "!profit");
        TransferHelper.safeTransfer(
            decoded.baseToken,
            address(pair),
            amountOwed
        );
        TransferHelper.safeTransfer(
            decoded.baseToken,
            decoded.payer,
            amountOwed - amountRecieved
        );
    }

    function uniswapV3FlashCallback(
        uint fee0,
        uint fee1,
        bytes calldata data
    ) external override {
        FlashV3CallbackData memory decoded = abi.decode(
            data,
            (FlashV3CallbackData)
        );
        address token0 = IUniswapV3Pool(msg.sender).token0();
        address token1 = IUniswapV3Pool(msg.sender).token1();
        uint24 fee = IUniswapV3Pool(msg.sender).fee();
        CallbackValidation.verifyCallback(
            routerV3.factory(),
            PoolAddress.PoolKey({token0: token0, token1: token1, fee: fee})
        );

        TransferHelper.safeApprove(
            decoded.baseToken,
            address(routerV3),
            decoded.amount
        );

        // swap for the tokenB
        console.log(fee, decoded.amount);
        uint amountB = routerV3.exactInputSingle(
            ISwapRouter.ExactInputSingleParams({
                tokenIn: token0 == decoded.baseToken ? token0 : token1,
                tokenOut: token0 == decoded.baseToken ? token1 : token0,
                fee: fee,
                recipient: address(this),
                deadline: block.timestamp,
                amountIn: decoded.amount,
                amountOutMinimum: 0,
                sqrtPriceLimitX96: 0
            })
        );

        // swap for the tokenC
        TransferHelper.safeApprove(
            decoded.baseToken == token0 ? token1 : token0,
            address(routerV2),
            decoded.amount
        );

        uint amountReceived;
        {
            address[] memory path = new address[](2);
            path[0] = decoded.baseToken == token0 ? token1 : token0;
            path[1] = decoded.baseToken == token0 ? token0 : token1;
            amountReceived = routerV2.swapExactTokensForTokens(
                amountB,
                0,
                path,
                address(this),
                block.timestamp
            )[1];
        }

        uint amountOwed = LowGasSafeMath.add(
            decoded.amount,
            fee0 == 0 ? fee0 : fee1
        );
        require(amountReceived > amountOwed, "!profit");
        TransferHelper.safeApprove(
            decoded.baseToken,
            address(this),
            amountOwed
        );

        // return tokens back
        pay(decoded.baseToken, address(this), msg.sender, amountOwed);

        // return the profit
        uint profit = LowGasSafeMath.sub(amountReceived, amountOwed);

        TransferHelper.safeApprove(decoded.baseToken, address(this), profit);
        pay(decoded.baseToken, address(this), decoded.payer, profit);
    }
}

